def goffmanPoint(rept):
    return round(((-1 + math.sqrt(1 + 8 * rept)) / 2), 0)

def RemoveAccents(data):
    return ''.join(x for x in unicodedata.normalize('NFKD', data) if x in string.ascii_letters).upper()



